<span class="line"><span style="color:#89DDFF;">UPDATE</span><span style="color:#EEFFFF;"> </span><span style="color:#FFCB6B;">account:</span><span style="color:#C792EA;">two</span><span style="color:#EEFFFF;"> </span><span style="color:#89DDFF;">SET</span><span style="color:#EEFFFF;"> </span><span style="color:#FFCB6B;">balance</span><span style="color:#EEFFFF;"> </span><span style="color:#89DDFF;">-=</span><span style="color:#EEFFFF;"> 300.00;</span></span>
